import React, {Component} from 'react';
import TextField from '@mui/material/TextField';
import Button from '@material-ui/core/Button';
import '../css/Home.css';
import appstore from '../images/appstore.svg';
import google from '../images/google.svg';

class Home extends Component{

  constructor() {
    super();
    this.state = {
      name: '',
      roomCode: '',
    };
  };

  setName = (event) => {
      this.setState({
        name: event.target.value
    });
  };

  setRoomCode = (event) => {
      this.setState({
        roomCode: event.target.value
    });
    console.log(this.state.roomCode);
  };

  render()
  {
    return (
      <section className ="section">
        <div className ="Left">
          <div className ="containerLeft">
            <div className="textField">
              <TextField id="outlined-basic" label="Name" variant="outlined" value={this.state.name} onChange={this.setName}/>
            </div>
            <TextField id="outlined-basic" label="Room Code" variant="outlined" value={this.state.roomCode} onChange={this.setRoomCode}/>
          </div>
        </div>

        <div className = "Middle">
          <Button className = "StartGame" style={{color:'white',backgroundColor:'#00c500'}} onClick={()=>{console.log('I WORK')}}> START GAME </Button>
        </div>

        <div className ="Right">
          <div className ="containerRight">
            <h2 className="h2"> Play in 3D </h2>
            <img src={appstore} alt="Appstore" className="image" />

            <img src={google} alt="Google Store" className="image2" />
          </div>
        </div>
      </section>
    );
  }
}

export default Home;
